"use strict";

/* бургер */
document.addEventListener("DOMContentLoaded", function () {
  document.getElementById("burger").addEventListener("click", function () {
    document.querySelector(".header-2").classList.toggle("open");
  });
});

/* поиск */
document.addEventListener('DOMContentLoaded', function (e) {
  document.getElementById('open-search-form').addEventListener('click', function (e) {
    document.getElementById('search-form').classList.add('search-form_show');
    //  alert('Сейчас обязательно найдем!')
  }), document.getElementById('search-form-close').addEventListener('click', function (e) {
    document.getElementById('search-form').classList.remove('search-form_show');
  });
  document.getElementById('search-form').addEventListener('submit', function (e) {
    e.preventDefault();
  });
});

/* валидация формы "Подписаться на рассылку" */
document.addEventListener("DOMContentLoaded", function () {
  var validator = new JustValidate('#info-form');
  validator.addField('#email', [{
    rule: 'required',
    errorMessage: 'Поле пустое'
  }, {
    rule: 'email',
    errorMessage: 'Недоступный формат'
  }]).onSuccess(function (e) {
    alert("Вы подписались на рассылку! Информация отправлена на почту!");
  });
});
/* валидация формы "Оставить заявку" */
document.addEventListener("DOMContentLoaded", function () {
  var validator = new JustValidate('.contacts__block-place');
  validator.addField('.contacts-name', [{
    rule: 'required',
    errorMessage: 'Поле пустое'
  }, {
    rule: 'minLength',
    value: 3,
    errorMessage: 'Минимальное число букв равно трем'
  }]).addField('.contacts-email', [{
    rule: 'required',
    errorMessage: 'Поле пустое'
  }, {
    rule: 'email',
    errorMessage: 'Недоступный формат'
  }]).onSuccess(function (e) {
    alert("Ваша заявка принята! Информация отправлена на почту!");
  });
});
